
public class AxelArroyoGimenezNumerosEntre1y20 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*
		 * Algorisme que mostri els números entre 1 i 20
		 * 
		 */

		// Creamos las variables
		int numeroA = 0;
		int numeroB = 21;
		int numero = 0;

		numero = numeroA + 1;
		
		System.out.println("Los números entre 1 y 20 son: ");
		while (numeroA < numero && numeroB > numero) {
			System.out.println(numero);
			numero++;
		}
		
	}

}
